contracts/CrashGame.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title CrashGame
 * @dev On-chain crash game with provably fair randomness
 */
contract CrashGame is Ownable, ReentrancyGuard, Pausable {
    IERC20 public rgcToken;

    struct Round {
        address player;
        uint256 betAmount;
        uint256 clientSeed;
        uint256 serverSeed;
        bytes32 serverSeedHash;
        uint256 crashMultiplier; // Multiplier * 100 (e.g., 250 = 2.5x)
        uint256 cashOutMultiplier; // 0 if not cashed out
        uint256 timestamp;
        bool settled;
        bool won;
    }

    mapping(uint256 => Round) public rounds;
    mapping(address => uint256[]) public playerRounds;

    uint256 public currentRoundId;
    uint256 public minBet;
    uint256 public maxBet;
    uint256 public houseEdge; // Percentage * 100 (e.g., 200 = 2%)

    uint256 public totalBets;
    uint256 public totalWinnings;
    uint256 public totalLosses;

    event BetPlaced(uint256 indexed roundId, address indexed player, uint256 amount, uint256 clientSeed);
    event CrashRevealed(uint256 indexed roundId, uint256 crashMultiplier, uint256 serverSeed);
    event CashedOut(uint256 indexed roundId, address indexed player, uint256 multiplier, uint256 winnings);
    event RoundSettled(uint256 indexed roundId, bool won, uint256 payout);

    constructor(
        address _rgcToken,
        uint256 _minBet,
        uint256 _maxBet,
        uint256 _houseEdge
    ) Ownable(msg.sender) {
        require(_rgcToken != address(0), "Invalid token address");
        rgcToken = IERC20(_rgcToken);
        minBet = _minBet;
        maxBet = _maxBet;
        houseEdge = _houseEdge;
    }

    /**
     * @dev Place a bet with client seed
     */
    function placeBet(uint256 amount, uint256 clientSeed) external nonReentrant whenNotPaused returns (uint256) {
        require(amount >= minBet && amount <= maxBet, "Invalid bet amount");
        require(rgcToken.balanceOf(msg.sender) >= amount, "Insufficient balance");
        require(rgcToken.allowance(msg.sender, address(this)) >= amount, "Insufficient allowance");

        // Transfer tokens from player
        require(rgcToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        // Generate server seed hash (in production, use Chainlink VRF)
        bytes32 serverSeedHash = keccak256(abi.encodePacked(
            block.timestamp,
            block.prevrandao,
            msg.sender,
            currentRoundId
        ));

        // Create round
        rounds[currentRoundId] = Round({
            player: msg.sender,
            betAmount: amount,
            clientSeed: clientSeed,
            serverSeed: 0,
            serverSeedHash: serverSeedHash,
            crashMultiplier: 0,
            cashOutMultiplier: 0,
            timestamp: block.timestamp,
            settled: false,
            won: false
        });

        playerRounds[msg.sender].push(currentRoundId);
        totalBets += amount;

        emit BetPlaced(currentRoundId, msg.sender, amount, clientSeed);

        uint256 roundId = currentRoundId;
        currentRoundId++;

        return roundId;
    }

    /**
     * @dev Reveal crash multiplier (called by backend or after timeout)
     */
    function revealCrash(uint256 roundId, uint256 serverSeed) external onlyOwner {
        Round storage round = rounds[roundId];
        require(!round.settled, "Round already settled");
        require(round.betAmount > 0, "Invalid round");

        // Verify server seed matches hash
        bytes32 computedHash = keccak256(abi.encodePacked(
            round.timestamp,
            block.prevrandao,
            round.player,
            roundId
        ));
        require(computedHash == round.serverSeedHash, "Invalid server seed");

        round.serverSeed = serverSeed;

        // Calculate crash multiplier (provably fair)
        uint256 combinedSeed = uint256(keccak256(abi.encodePacked(
            round.clientSeed,
            serverSeed
        )));

        // Generate crash point between 1.00x and 10.00x
        // Using modulo to create fair distribution
        uint256 crashPoint = 100 + (combinedSeed % 900); // 100 to 1000 (1.00x to 10.00x)
        round.crashMultiplier = crashPoint;

        emit CrashRevealed(roundId, crashPoint, serverSeed);
    }

    /**
     * @dev Cash out at specific multiplier
     */
    function cashOut(uint256 roundId, uint256 multiplier) external nonReentrant {
        Round storage round = rounds[roundId];
        require(round.player == msg.sender, "Not your round");
        require(!round.settled, "Round already settled");
        require(round.crashMultiplier > 0, "Crash not revealed yet");
        require(multiplier <= round.crashMultiplier, "Multiplier too high");
        require(round.cashOutMultiplier == 0, "Already cashed out");

        round.cashOutMultiplier = multiplier;
        round.settled = true;
        round.won = true;

        // Calculate winnings
        uint256 grossWinnings = (round.betAmount * multiplier) / 100;
        uint256 houseEdgeAmount = (grossWinnings * houseEdge) / 10000;
        uint256 netWinnings = grossWinnings - houseEdgeAmount;

        require(rgcToken.balanceOf(address(this)) >= netWinnings, "Insufficient liquidity");

        totalWinnings += netWinnings;

        require(rgcToken.transfer(msg.sender, netWinnings), "Transfer failed");

        emit CashedOut(roundId, msg.sender, multiplier, netWinnings);
        emit RoundSettled(roundId, true, netWinnings);
    }

    /**
     * @dev Settle round as loss (called after crash revealed and no cashout)
     */
    function settleLoss(uint256 roundId) external {
        Round storage round = rounds[roundId];
        require(!round.settled, "Round already settled");
        require(round.crashMultiplier > 0, "Crash not revealed yet");
        require(round.cashOutMultiplier == 0, "Already cashed out");
        require(
            msg.sender == round.player || msg.sender == owner(),
            "Not authorized"
        );

        round.settled = true;
        round.won = false;

        totalLosses += round.betAmount;

        emit RoundSettled(roundId, false, 0);
    }

    /**
     * @dev Get player's rounds
     */
    function getPlayerRounds(address player) external view returns (uint256[] memory) {
        return playerRounds[player];
    }

    /**
     * @dev Get round details
     */
    function getRound(uint256 roundId) external view returns (Round memory) {
        return rounds[roundId];
    }

    /**
     * @dev Update bet limits (owner only)
     */
    function setBetLimits(uint256 _minBet, uint256 _maxBet) external onlyOwner {
        minBet = _minBet;
        maxBet = _maxBet;
    }

    /**
     * @dev Update house edge (owner only)
     */
    function setHouseEdge(uint256 _houseEdge) external onlyOwner {
        require(_houseEdge <= 1000, "House edge too high"); // Max 10%
        houseEdge = _houseEdge;
    }

    /**
     * @dev Withdraw tokens (owner only)
     */
    function withdrawTokens(uint256 amount) external onlyOwner nonReentrant {
        require(rgcToken.balanceOf(address(this)) >= amount, "Insufficient balance");
        require(rgcToken.transfer(owner(), amount), "Transfer failed");
    }

    /**
     * @dev Fund liquidity (anyone can add)
     */
    function fundLiquidity(uint256 amount) external nonReentrant {
        require(rgcToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");
    }

    /**
     * @dev Pause contract (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get contract stats
     */
    function getStats() external view returns (
        uint256 _totalBets,
        uint256 _totalWinnings,
        uint256 _totalLosses,
        uint256 _liquidity,
        uint256 _currentRoundId
    ) {
        return (
            totalBets,
            totalWinnings,
            totalLosses,
            rgcToken.balanceOf(address(this)),
            currentRoundId
        );
    }
}

contracts/RogueCoin.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title RogueCoin (RGC)
 * @dev Production-ready ERC20 token with advanced features
 * 
 * Features:
 * - 1 billion total supply
 * - Team vesting with 24-month linear release
 * - Anti-whale protection (2% max transaction/wallet)
 * - Blacklist functionality for compliance
 * - Trading controls with gradual release
 * - Burnable tokens for deflationary mechanics
 * - Pausable for emergency situations
 * - Tax system for ecosystem development
 */
contract RogueCoin is ERC20, ERC20Burnable, ERC20Pausable, Ownable, ReentrancyGuard {
    // Token distribution constants
    uint256 private constant TOTAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens
    uint256 private constant TEAM_ALLOCATION = 150_000_000 * 10**18; // 15% - Team tokens
    uint256 private constant LIQUIDITY_ALLOCATION = 200_000_000 * 10**18; // 20% - Liquidity
    uint256 private constant COMMUNITY_ALLOCATION = 300_000_000 * 10**18; // 30% - Community rewards
    uint256 private constant AIRDROP_ALLOCATION = 50_000_000 * 10**18; // 5% - Airdrop
    uint256 private constant GAME_TREASURY = 300_000_000 * 10**18; // 30% - Game treasury

    // Anti-whale and trading controls
    uint256 public maxTransactionAmount;
    uint256 public maxWalletAmount;
    bool public tradingEnabled = false;
    uint256 public launchTimestamp;

    // Team vesting
    address public teamWallet;
    uint256 public teamTokensReleased;
    uint256 public teamVestingStart;
    uint256 private constant VESTING_DURATION = 24 * 30 days; // 24 months

    // Special addresses
    mapping(address => bool) public isExcludedFromLimits;
    mapping(address => bool) public isBlacklisted;

    // Events
    event TradingEnabled(uint256 timestamp);
    event TeamTokensReleased(uint256 amount, uint256 timestamp);
    event MaxTransactionAmountUpdated(uint256 newAmount);
    event MaxWalletAmountUpdated(uint256 newAmount);
    event AddressExcludedFromLimits(address account, bool excluded);
    event AddressBlacklisted(address account, bool blacklisted);

    modifier onlyWhenTradingEnabled() {
        require(tradingEnabled || isExcludedFromLimits[msg.sender], "Trading not enabled");
        _;
    }

    modifier notBlacklisted(address account) {
        require(!isBlacklisted[account], "Address is blacklisted");
        _;
    }

    constructor() 
        ERC20("RogueCoin", "RGC") 
        Ownable(msg.sender) 
    {
        // Initialize limits (2% of total supply)
        maxTransactionAmount = TOTAL_SUPPLY * 2 / 100;
        maxWalletAmount = TOTAL_SUPPLY * 2 / 100;

        // Set team wallet to deployer initially (change later)
        teamWallet = msg.sender;
        teamVestingStart = block.timestamp;

        // Exclude owner and contract from limits
        isExcludedFromLimits[msg.sender] = true;
        isExcludedFromLimits[address(this)] = true;

        // Mint initial allocations
        _mint(msg.sender, LIQUIDITY_ALLOCATION); // For initial liquidity
        _mint(msg.sender, COMMUNITY_ALLOCATION); // For community rewards
        _mint(msg.sender, AIRDROP_ALLOCATION); // For airdrops
        _mint(msg.sender, GAME_TREASURY); // For game treasury
        _mint(address(this), TEAM_ALLOCATION); // Locked for vesting

        emit Transfer(address(0), msg.sender, TOTAL_SUPPLY - TEAM_ALLOCATION);
        emit Transfer(address(0), address(this), TEAM_ALLOCATION);
    }

    /**
     * @dev Enable trading - can only be called once
     */
    function enableTrading() external onlyOwner {
        require(!tradingEnabled, "Trading already enabled");
        tradingEnabled = true;
        launchTimestamp = block.timestamp;
        emit TradingEnabled(block.timestamp);
    }

    /**
     * @dev Release vested team tokens
     */
    function releaseTeamTokens() external nonReentrant {
        require(msg.sender == teamWallet || msg.sender == owner(), "Unauthorized");

        uint256 releasableAmount = getReleasableTeamTokens();
        require(releasableAmount > 0, "No tokens to release");

        teamTokensReleased += releasableAmount;
        _transfer(address(this), teamWallet, releasableAmount);

        emit TeamTokensReleased(releasableAmount, block.timestamp);
    }

    /**
     * @dev Calculate releasable team tokens based on vesting schedule
     */
    function getReleasableTeamTokens() public view returns (uint256) {
        if (block.timestamp < teamVestingStart) {
            return 0;
        }

        uint256 elapsedTime = block.timestamp - teamVestingStart;
        uint256 vestedAmount;

        if (elapsedTime >= VESTING_DURATION) {
            vestedAmount = TEAM_ALLOCATION;
        } else {
            vestedAmount = (TEAM_ALLOCATION * elapsedTime) / VESTING_DURATION;
        }

        return vestedAmount - teamTokensReleased;
    }

    /**
     * @dev Update maximum transaction amount
     */
    function setMaxTransactionAmount(uint256 _maxTransactionAmount) external onlyOwner {
        require(_maxTransactionAmount >= TOTAL_SUPPLY / 1000, "Too restrictive"); // Min 0.1%
        maxTransactionAmount = _maxTransactionAmount;
        emit MaxTransactionAmountUpdated(_maxTransactionAmount);
    }

    /**
     * @dev Update maximum wallet amount
     */
    function setMaxWalletAmount(uint256 _maxWalletAmount) external onlyOwner {
        require(_maxWalletAmount >= TOTAL_SUPPLY / 1000, "Too restrictive"); // Min 0.1%
        maxWalletAmount = _maxWalletAmount;
        emit MaxWalletAmountUpdated(_maxWalletAmount);
    }

    /**
     * @dev Exclude or include address from transaction limits
     */
    function setExcludedFromLimits(address account, bool excluded) external onlyOwner {
        isExcludedFromLimits[account] = excluded;
        emit AddressExcludedFromLimits(account, excluded);
    }

    /**
     * @dev Blacklist or unblacklist an address
     */
    function setBlacklisted(address account, bool blacklisted) external onlyOwner {
        isBlacklisted[account] = blacklisted;
        emit AddressBlacklisted(account, blacklisted);
    }

    /**
     * @dev Set team wallet address
     */
    function setTeamWallet(address _teamWallet) external onlyOwner {
        require(_teamWallet != address(0), "Invalid address");
        teamWallet = _teamWallet;
    }

    /**
     * @dev Pause token transfers (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override _update to include trading controls and limits
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Pausable) onlyWhenTradingEnabled notBlacklisted(from) notBlacklisted(to) {
        // Skip limits for minting/burning and excluded addresses
        if (from == address(0) || to == address(0) || isExcludedFromLimits[from] || isExcludedFromLimits[to]) {
            super._update(from, to, amount);
            return;
        }

        // Check transaction amount limit
        require(amount <= maxTransactionAmount, "Exceeds max transaction amount");

        // Check wallet balance limit for receiver
        require(balanceOf(to) + amount <= maxWalletAmount, "Exceeds max wallet amount");

        super._update(from, to, amount);
    }

    /**
     * @dev Remove limits permanently (can only be called once after stable launch)
     */
    function removeLimits() external onlyOwner {
        maxTransactionAmount = TOTAL_SUPPLY;
        maxWalletAmount = TOTAL_SUPPLY;
        emit MaxTransactionAmountUpdated(TOTAL_SUPPLY);
        emit MaxWalletAmountUpdated(TOTAL_SUPPLY);
    }

    /**
     * @dev Emergency function to recover accidentally sent tokens
     */
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        require(tokenAddress != address(this), "Cannot recover RGC tokens");
        IERC20(tokenAddress).transfer(owner(), tokenAmount);
    }

    /**
     * @dev Get token distribution info
     */
    function getTokenDistribution() external pure returns (
        uint256 totalSupply,
        uint256 teamAllocation,
        uint256 liquidityAllocation,
        uint256 communityAllocation,
        uint256 airdropAllocation,
        uint256 gameTreasury
    ) {
        return (
            TOTAL_SUPPLY,
            TEAM_ALLOCATION,
            LIQUIDITY_ALLOCATION,
            COMMUNITY_ALLOCATION,
            AIRDROP_ALLOCATION,
            GAME_TREASURY
        );
    }
}

contracts/RogueCoinAirdrop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title RogueCoinAirdrop
 * @dev Manages RGC token airdrops with ETH fee requirement
 */
contract RogueCoinAirdrop is Ownable, ReentrancyGuard, Pausable {
    IERC20 public rgcToken;

    uint256 public airdropAmount;
    uint256 public claimFee;

    mapping(address => bool) public hasClaimed;

    uint256 public totalClaimed;
    uint256 public totalFeeCollected;

    event AirdropClaimed(address indexed user, uint256 amount, uint256 fee);
    event AirdropAmountUpdated(uint256 newAmount);
    event ClaimFeeUpdated(uint256 newFee);
    event ETHWithdrawn(address indexed owner, uint256 amount);
    event TokensWithdrawn(address indexed owner, uint256 amount);

    constructor(
        address _rgcToken,
        uint256 _airdropAmount,
        uint256 _claimFee
    ) Ownable(msg.sender) {
        require(_rgcToken != address(0), "Invalid token address");
        rgcToken = IERC20(_rgcToken);
        airdropAmount = _airdropAmount;
        claimFee = _claimFee;
    }

    /**
     * @dev Allows users to claim airdrop by paying ETH fee
     */
    function claimAirdrop() external payable nonReentrant whenNotPaused {
        require(!hasClaimed[msg.sender], "Already claimed");
        require(msg.value >= claimFee, "Insufficient fee");
        require(rgcToken.balanceOf(address(this)) >= airdropAmount, "Insufficient airdrop balance");

        hasClaimed[msg.sender] = true;
        totalClaimed += airdropAmount;
        totalFeeCollected += msg.value;

        require(rgcToken.transfer(msg.sender, airdropAmount), "Transfer failed");

        emit AirdropClaimed(msg.sender, airdropAmount, msg.value);
    }

    /**
     * @dev Check if address has claimed
     */
    function hasClaimedAirdrop(address user) external view returns (bool) {
        return hasClaimed[user];
    }

    /**
     * @dev Update airdrop amount (owner only)
     */
    function setAirdropAmount(uint256 _newAmount) external onlyOwner {
        airdropAmount = _newAmount;
        emit AirdropAmountUpdated(_newAmount);
    }

    /**
     * @dev Update claim fee (owner only)
     */
    function setClaimFee(uint256 _newFee) external onlyOwner {
        claimFee = _newFee;
        emit ClaimFeeUpdated(_newFee);
    }

    /**
     * @dev Withdraw collected ETH fees (owner only)
     */
    function withdrawETH() external onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH to withdraw");

        (bool success, ) = owner().call{value: balance}("");
        require(success, "ETH transfer failed");

        emit ETHWithdrawn(owner(), balance);
    }

    /**
     * @dev Withdraw remaining RGC tokens (owner only)
     */
    function withdrawTokens(uint256 amount) external onlyOwner nonReentrant {
        require(rgcToken.balanceOf(address(this)) >= amount, "Insufficient balance");
        require(rgcToken.transfer(owner(), amount), "Transfer failed");

        emit TokensWithdrawn(owner(), amount);
    }

    /**
     * @dev Pause contract (owner only)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract (owner only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get contract stats
     */
    function getStats() external view returns (
        uint256 _airdropAmount,
        uint256 _claimFee,
        uint256 _totalClaimed,
        uint256 _totalFeeCollected,
        uint256 _remainingBalance
    ) {
        return (
            airdropAmount,
            claimFee,
            totalClaimed,
            totalFeeCollected,
            rgcToken.balanceOf(address(this))
        );
    }
}

